<!DOCTYPE html><html lang="en">  <head>  <meta charset="utf-8">  <meta name="viewport" content="width=device-width, initial-scale=1">  <!--<link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">-->  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js"></script>  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0-beta/css/bootstrap.min.css" integrity="sha384-/Y6pD6FV/Vv2HJnA6t+vslU6fwYXjCFtcEpHbNJ0lyAFsXTsjBbfaDjzALeQsN6M" crossorigin="anonymous"><!DOCTYPE HTML><html lang="en-US"><head>	<title>Shop</title>	<meta charset="UTF-8">	<meta name="viewport" content="width=device-width, initial-scale=1">  <!--<link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/css/bootstrap.min.css">-->  <script src="https://ajax.googleapis.com/ajax/libs/jquery/3.2.1/jquery.min.js"></script>  <script src="https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js"></script>  <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0-beta/css/bootstrap.min.css" integrity="sha384-/Y6pD6FV/Vv2HJnA6t+vslU6fwYXjCFtcEpHbNJ0lyAFsXTsjBbfaDjzALeQsN6M" crossorigin="anonymous">	<style type="text/css">		body {			font: 13px Arial;		}		#products {			text-align: center; float: left;		}		#products ul {			list-style-type: none; margin: 0px;		}		#products li {			width: 150px; padding: 4px; margin: 8px;			border: 1px solid #ddd; background-color: #eee;			-moz-border-radius: 4px; -webkit-border-radius: 4px;		}		#products .name {			font-size: 15px; margin: 5px;		}		#products .price {			margin: 5px;		}		#products .option {			margin: 5px;		}				#cart {			padding: 4px; margin: 8px; float: left;			border: 1px solid #ddd; background-color: #eee;			-moz-border-radius: 4px; -webkit-border-radius: 4px;		}		#cart table {			width: 320px; border-collapse: collapse;			text-align: left;		}		#cart th {			border-bottom: 1px solid #aaa;					}		#cart caption {			font-size: 15px; height: 30px; text-align: left;		}		#cart .total {			height: 40px;		}		#cart .remove a {			color: red;		} 	</style></head><body><div class="container">    <section id="main">      <div class="container">        <div class="row">		<div class="col-md-6">        <?php if ($cart = $this->cart->contents()): ?>	<div id="cart">		<table>		<caption>Shopping Cart</caption>		<thead>			<tr>				<th>Item Name</th>				<th>Qty</th>				<th>Price</th>				<th></th>			</tr>		</thead>		<?php foreach ($cart as $item): ?>			<tr>				<td><?php echo $item['name']; ?></td>				<td><?php echo $item['qty']; ?></td>				<td>$<?php echo $item['subtotal']; ?></td>				<td class="remove">					<?php echo anchor('shop/remove/'.$item['rowid'],'X'); ?>				</td>			</tr>		<?php endforeach; ?>		<tr class="total">			<td colspan="2"><strong>Total</strong></td>			<td>$<?php echo $this->cart->total(); ?></td>		</tr>		</table>			</div>	<?php endif; ?>        </div>                   <div class="col-md-6">            <div class="panel panel-default">              <div class="panel-heading main-color-bg">                              </div>                <br>             <div class="table-responsive">             <table class="table table-bordered">      <div id="products">	<ul>		<?php foreach ($products as $product): ?>		<li>			<?php echo form_open('shop/add'); ?>			<div class="name"><?php echo $product->p_name; ?></div>			<div class="thumb">			<?php echo img(array(				'src' => 'images/' . $product->p_image,				'class' => 'thumb',				'alt' => $product->p_name			)); ?>							</div>			<div class="price">$<?php echo $product->p_price; ?></div>						<?php echo form_hidden('id', $product->p_id); ?>			<?php echo form_submit('action', 'Add to Cart'); ?>			<?php echo form_close(); ?>		</li>		<?php endforeach; ?>	</ul>	</div>           </table>                   </div>              </div>          </div>        </div>      </div>    </section></div>      <footer id="footer"><center>        <br/><br/><p>Copyright  &copy; 2017</p>    </center></footer>